const headerBtn=document.querySelector(".header__btn"),loginModal=document.querySelector(".login-modal"),loginModalClose=document.querySelector(".login-modal-close");headerBtn.addEventListener("click",()=>{loginModal.classList.toggle("open"),document.body.classList.toggle("lock")}),loginModalClose.addEventListener("click",()=>{loginModal.classList.toggle("open"),document.body.classList.toggle("lock")});const passwordRecoveryModal=document.querySelector(".password-recovery-modal"),passwordRecoveryModalOpen=document.querySelector(".password-recovery-modal-open"),passwordRecoveryModalClose=document.querySelector(".password-recovery-modal-close");passwordRecoveryModalOpen.addEventListener("click",()=>{loginModal.classList.toggle("open"),passwordRecoveryModal.classList.toggle("open")}),passwordRecoveryModalClose.addEventListener("click",()=>{passwordRecoveryModal.classList.toggle("open"),document.body.classList.toggle("lock")});